h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: 'Open Sans', sans-serif;
  font-weight: 700;
  line-height: 1;
  margin-top: 0;
  margin-bottom: 20px;
  text-rendering: optimizeLegibility;
}
h1 {
  font-size: 4.5rem;
  letter-spacing: -2px;
  line-height: 1.2em;
  text-indent: -3px;
}
h2 {
  font-size: 3.9rem;
  letter-spacing: -1px;
  line-height: 1.2em;
  text-indent: -2px;
}
h3 {
  font-size: 3.3rem;
}
h4 {
  font-size: 2.8rem;
  margin-bottom: 0;
}
h5 {
  font-size: 2.4rem;
}
h6 {
  font-size: 2rem;
}
a {
  -webkit-transition: color ease 0.3s;
  -moz-transition: color ease 0.3s;
  -o-transition: color ease 0.3s;
  -ms-transition: color ease 0.3s;
  transition: color ease 0.3s;
  text-decoration: none;
}
a:hover {
  color: $link-article-color;
}
hr {
  display: block;
  height: 1px;
  border: 0;
  border-top: #d3d3d3 1px solid;
  margin: 40px 0;
  padding: 0;
}
hr:after {
  display: block;
  content: '';
  width: 7px;
  height: 7px;
  border: 1px solid #dadada 1px solid;
  position: relative;
  top: -5px;
  left: 50%;
  margin-left: -5px;
  background: #d3d3d3;
  border-radius: 100%;
  -webkit-box-shadow: #f8f8f8 0 0 0 5px;
  box-shadow: #f8f8f8 0 0 0 5px;
}
p,
ul,
ol,
dl,
table {
  margin: 20px 0;
}
ol ol,
ul ul,
ul ol,
ol ul {
  margin: 5px 0;
}
dl dt {
  clear: left;
  float: left;
  font-weight: bold;
  margin-bottom: 12px;
  overflow: hidden;
  text-align: right;
  -o-text-overflow: ellipsis;
  text-overflow: ellipsis;
  white-space: nowrap;
  width: 180px;
}
dl dd {
  margin-left: 200px;
  margin-bottom: 12px;
}
code,
tt {
  background: #1d9cf7;
  border-radius: 0.2em;
  color: #fafafa;
  font-family: 'Fira Mono', monospace, sans-serif;
  font-size: 0.85em;
  font-weight: 400;
  padding: 1px 3px;
  white-space: pre-wrap;
}
pre {
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: #222;
  border-radius: 0.3em;
  color: #fafafa;
  font-family: 'Fira Mono', monospace, sans-serif;
  font-size: 0.9em;
  font-weight: 400;
  margin: 1.6em 0;
  overflow: auto;
  padding: 10px;
  white-space: pre;
  width: 100%;
}
pre code,
tt {
  background: transparent;
  border: none;
  font-size: inherit;
  padding: 0;
  white-space: -moz-pre-wrap;
  white-space: pre-wrap;
}
kbd {
  background: #f4f4f4;
  border: 1px solid #ccc 1px solid;
  border-radius: 3px;
  -webkit-box-shadow: 0 1px 0 rgba(0,0,0,0.2), 0 0 0 2px #fafafa inset;
  box-shadow: 0 1px 0 rgba(0,0,0,0.2), 0 0 0 2px #fafafa inset;
  color: #666;
  display: inline-block;
  font-family: 'Droid Serif', Georgia, serif;
  font-size: 0.9em;
  margin-bottom: 0.4em;
  padding: 0 5px;
  text-shadow: 0 1px 0 #fafafa;
  white-space: nowrap;
}
iframe,
.fluid-width-video-wrapper {
  display: block;
  margin: 1.6em 0;
}
/* When a video is inside the fitvids wrapper, drop the
margin on the iframe, cause it breaks stuff. */
.fluid-width-video-wrapper iframe {
  margin: 0;
}
/* Hexo: Youtube, other video container */
.video-container {
  position: relative;
  padding-top: 56.25%;
  height: 0;
  overflow: hidden;
}
.video-container iframe,
.video-container object,
.video-container embed {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  margin-top: 0;
}
table > tbody > tr > td {
  padding-right: 10px;
}
/* Hexo: Syntax Highlighter */
figure.highlight {
  background: #222;
  border-radius: 0.3em;
  border: 1px solid #f2f2f2;
  color: #f2f2f2;
  font-size: 0.9em;
  line-height: 1.45em;
  margin: 1em 0 2.1em 0;
  overflow: auto;
  white-space: pre;
  word-wrap: normal;
}
figure.highlight.md td.gutter {
  display: none;
}
figure.highlight figcaption {
  padding: 7px 10px;
  font-family: 'Open Sans', sans-serif;
  font-size: 0.8em;
  color: #839496;
  background: #191919;
  text-align: center;
}
figure.highlight figcaption a {
  margin-left: 0.3em;
}
figure.highlight table {
  margin: 0;
}
figure.highlight table > tbody > tr > td {
  padding: 0;
  background: #fafafa;
}
figure.highlight table > tbody > tr > td.gutter {
  background-color: #222;
  max-width: 40px;
  text-align: right;
}
figure.highlight table > tbody > tr > td.gutter pre span.line {
  color: #839496;
}
figure.highlight pre {
  border: none;
  border-radius: 0;
  margin: 0;
}
@media (min-width: 720px) {
  body {
    display: -webkit-box;
    display: -moz-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: box;
    display: flex;
    -webkit-box-orient: horizontal;
    -moz-box-orient: horizontal;
    -o-box-orient: horizontal;
    -webkit-flex-direction: row;
    -ms-flex-direction: row;
    flex-direction: row;
  }
  body header {
    -webkit-box-flex: 1;
    -moz-box-flex: 1;
    -o-box-flex: 1;
    box-flex: 1;
    -webkit-flex: 1;
    -ms-flex: 1;
    flex: 1;
    overflow: hidden;
    text-align: right;
    padding: 40px;
  }
  body header img {
    margin: 0 -32px 0 0;
  }
  body header nav ul {
    padding-top: 24px;
  }
  body header nav ul li {
    display: block;
    line-height: 1.6em;
    padding: 0;
  }
  body header nav ul li:after {
    content: '';
    padding: 0;
  }
  body main {
    -webkit-box-flex: 2;
    -moz-box-flex: 2;
    -o-box-flex: 2;
    box-flex: 2;
    -webkit-flex: 2;
    -ms-flex: 2;
    flex: 2;
    padding: 40px;
  }
  body main form .form-column input[type="name"],
  body main form .form-column input[type="email"] {
    width: 35%;
    margin: 0.5em 0.5em 0.5em 0;
  }
}
